		einfuegeAnweisung.addBatch("INSERT INTO WAFFEN (WAFFEN_NAME, HELD_NAME) VALUES ('Schwert', 'Jappi')");
		einfuegeAnweisung.addBatch("INSERT INTO WAFFEN (WAFFEN_NAME, HELD_NAME) VALUES ('Schwert', 'Jappi')");
		einfuegeAnweisung.addBatch("INSERT INTO WAFFEN (WAFFEN_NAME, HELD_NAME) VALUES ('Zauberstab', 'Joppi')");;
