		//			PreparedStatement abfrageAnweisung = verbindung.prepareStatement("SELECT * FROM UNSERE_HELDEN");
		//			PreparedStatement abfrageAnweisung = verbindung.prepareStatement("SELECT * FROM UNSERE_HELDEN WHERE STAERKE >= 10");
